package com.afsal.dev.filimapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.filimapp.databinding.BaseRvItemBindingimport com.afsal.dev.filimapp.models.MovieItemclass BaseMoviesAdapter(private val moviesList:List<MovieItem>) : RecyclerView.Adapter<BaseMoviesAdapter.ItemHolder>() {    val list = listOf(        "Movie1",        "Movie2",        "Movie3",        "Movie4",        "Movie5",        "Movie6",        "Movie7",        "Movie8",        "Movie9"    )    inner class ItemHolder(val binding: BaseRvItemBinding) :        RecyclerView.ViewHolder(binding.root) {          init {          }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            BaseRvItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item = moviesList[position]            holder.binding.tittle.text=item.name    }    override fun getItemCount() =moviesList.size}