package com.afsal.dev.filimapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.filimapp.databinding.BaseRvItemBindingimport com.afsal.dev.filimapp.models.MovieItemimport com.bumptech.glide.Glideclass BaseMoviesAdapter(private val moviesList: List<MovieItem>) :    RecyclerView.Adapter<BaseMoviesAdapter.ItemHolder>() {    inner class ItemHolder(val binding: BaseRvItemBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(movie: MovieItem) {            binding.tittle.text = movie.title            binding.releasDate.text = movie.year            Glide                .with(binding.posterImage.context)                .load(movie.stream_icon)                // .centerCrop()                .fitCenter()                // .placeholder(R.drawable.image_place_holder)                .into(binding.posterImage)        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            BaseRvItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item = moviesList[position]        holder.bind(item)    }    override fun getItemCount() = moviesList.size}