package com.afsal.dev.filimapp.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.AsyncListDifferimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.RecyclerViewimport com.afsal.dev.filimapp.databinding.GalleryItemBindingimport com.afsal.dev.filimapp.databinding.TopRvItemBindingimport com.afsal.dev.filimapp.models.CategoriesListDataimport com.afsal.dev.filimapp.models.MovieItemimport com.afsal.dev.typicodapp.uttil.loadImageimport com.bumptech.glide.Glideclass GalleryAdapter() : RecyclerView.Adapter<GalleryAdapter.ItemHolder>() {    private val diffCallback = object : DiffUtil.ItemCallback<MovieItem>() {        override fun areItemsTheSame(oldItem: MovieItem, newItem: MovieItem): Boolean {            return oldItem.category_id== newItem.category_id        }        override fun areContentsTheSame(oldItem: MovieItem, newItem: MovieItem): Boolean {            return oldItem == newItem        }    }    val differ = AsyncListDiffer(this, diffCallback)    inner class ItemHolder(val binding: GalleryItemBinding) :        RecyclerView.ViewHolder(binding.root) {          fun bind(movie: MovieItem){              Glide                  .with(binding.posterImage.context)                  .load(movie.stream_icon)                   //.centerCrop()                  .centerInside()                  //.fitCenter()                  // .placeholder(R.drawable.image_place_holder)                  .into(binding.posterImage)              binding.tittle.text=movie.title              binding.releasDate.text=movie.year          }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ItemHolder {        return ItemHolder(            GalleryItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun onBindViewHolder(holder: ItemHolder, position: Int) {        val item = differ.currentList[position]            holder.bind(item)    }    override fun getItemCount() =differ.currentList.size}