package com.afsal.dev.filimapp.diimport com.afsal.dev.filimapp.helper.Constants.BASE_URLimport com.afsal.dev.filimapp.network.ApiServiceimport com.afsal.dev.filimapp.repository.MoviesRepositoryimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport retrofit2.createimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)object AppModule {    @Singleton    @Provides    fun provideRetrofitInstance():Retrofit =        Retrofit.Builder().                baseUrl(BASE_URL)            .addConverterFactory(GsonConverterFactory.create())            .build()    @Singleton    @Provides    fun provideApiService(retrofit: Retrofit):ApiService=        retrofit.create(ApiService::class.java)    @Provides    fun provideRepository(apiService: ApiService)=MoviesRepository(apiService)}