package com.afsal.dev.typicodapp.uttilimport android.content.Contextimport android.util.Logimport android.view.Viewimport android.widget.ImageViewimport androidx.fragment.app.Fragmentimport com.afsal.dev.filimapp.Rimport com.afsal.dev.filimapp.network.Resourceimport com.bumptech.glide.Glideimport com.google.android.material.snackbar.Snackbarfun View.showSnackBar(message:String,action:(()->Unit)?=null){    val snackbar=Snackbar.make(this,message,Snackbar.LENGTH_LONG)    action?.let {        snackbar.setAction("Retry"){            it()        }    }    snackbar.show()}fun Fragment.handleApiError(failure: Resource.Failure, retry:(()->Unit)?= null ){    Log.d("TTTT"," error code${failure.errorCode}")    when{        failure.isNetworkError ==true ->requireView().showSnackBar("Pleas check your Internet connection !!",retry)       else ->{           val error= failure.errorBody?.toString().toString()           requireView().showSnackBar(error)       }    }}fun loadImage(imgUrl:String, imageView: ImageView) {    Glide.with(imageView.context)        .load(imgUrl)        //.centerCrop()        .placeholder(R.color.teal_200)        .fitCenter()        .into(imageView)}fun loadImageToImageview(imgUrl: String,imageView:ImageView){    Glide.with(imageView.context)        .load(imgUrl)        .placeholder(R.drawable.image_place_holder) // any placeholder to load at start        .error(R.drawable.tittle_bg)  // any image in case of error       // .override(200, 200) // resizing        .centerCrop()        .into(imageView);}